<div id="comments-wrapper"><?php if (have_comments()) : foreach (get_comments() as $comment): ?><div id="<?php comment_ID(); ?>" class="individual-comment"><span class="comment-avatar"><?php echo get_avatar( $comment, 40 ); ?></span><span class="comment-author"><?php echo get_comment_author_link( $comment_ID ); ?></span><span class="comment-body"><?php comment_text(); ?></span><span class="comment-meta"><?php comment_date('jS F Y'); ?>, <?php comment_time(); ?></span></div><?php endforeach; endif; ?></div>
