<div id="pagination-container"><span class="newer-posts" title="Newer posts"><?php previous_posts_link('Newer posts'); ?></span><span class="older-posts" title="Older posts"><?php next_posts_link('Older posts'); ?></span><?php $paged_maxnum=$GLOBALS['wp_query']->max_num_pages;if($paged_maxnum>1){$paged_current_page=get_query_var('paged')?intval(get_query_var('paged')):1;$title_page_nrs='<div id="pagination"><p>Page <span>'.$paged_current_page.'</span> of <span>'.$paged_maxnum.'</span></p></div>';echo $title_page_nrs;} ?></div>
